import React from 'react';
import $ from 'jquery'

window.jQuery = $;
window.$ = $;
global.jQuery = $;

export class Channel extends React.Component {

    click = (e) => {
        this.props.onClick(this.props.id);
        $(e).addClass("channel-itemSelected");
    }

    render() {
        return (
            <div id='channel' className='channel-item' onClick={this.click}>
                <div>{this.props.name}</div>
                <span>{this.props.participants}</span>
            </div>
        )
    }
}
