import {React} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import './App.css';
import socketClient  from "socket.io-client";
import { Chat } from './chat/Chat';
import { Login } from './Login';
import $ from 'jquery'

window.jQuery = $;
window.$ = $;
global.jQuery = $;

const SERVER = "http://127.0.0.1:8080";

class App extends React.Component {

  constructor(){
    super();
    this.state = '';
    this.handleLogout = this.handleLogout.bind(this);
    this.handleSetCurrentUser = this.handleSetCurrentUser.bind(this);
    var socket = socketClient (SERVER);

    socket.on('connection', () => {
      console.log(`I'm connected with the back-end`);
    });
  }

    handleLogout = () => {

      fetch('/logout');
      window.location.href = '/login';

    }

    handleSetCurrentUser = (userName) => {
        this.setState(userName);
    }

    render(){
      return (

          <div className="App">

            <Router>
            <Link to="/login">Login</Link>
            &nbsp;&nbsp;
            <Link onClick={() => this.handleLogout()}>Logout</Link>
            <br/><br/>
            <Switch>
            <Route path="/login/" exact component={Login} currentUser={this.state} setCurrentUser={this.handleSetCurrentUser}/>
            <Route path="/chat/chat/" exact component={Chat} currentUser={this.state}/>
            </Switch>
            </Router>
          </div>

      );
    }
    }

export default App;
